sp_widget.client_script:
function($scope,$rootScope, spModal) {
	/* widget controller */
	var c = this;
	var flg = 0;

	$rootScope.$on('selectNote', function(event,data) {
		c.server.get({
			action: 'getNote',
			noteID: $rootScope.noteID
		}).then(function(r) {
			c.data.title = r.data.note.title;
			c.data.note = r.data.note.note;
			c.data.noteID = r.data.note.sys_id;
		});
	});

	c.updateNote = function(updateType) {
		c.server.get({
			action: 'updateNote',
			noteID: c.data.noteID,
			noteBody: c.data.note,
			noteTitle: c.data.title
		}).then(function(r) {
			// When the promise is returned, emit the change
			if (updateType == 'title' || updateType == 'body') { 
				$rootScope.$emit('updateTitle', r.data); 
			}
		});
	}
	c.deleteNote = function() {
		c.server.get({
			action: 'deleteNote',
			noteID: c.data.noteID
		}).then(function(r) {
			$rootScope.$emit('deleteNote', c.data.noteID);
			c.data.title = '';
			c.data.note = '';
			c.data.noteID = '';
		});
	}
	c.confirmDelete = function () {
		spModal.confirm("Are you sure you want to delete this Note record?")
			.then(function(option){
			if(option){
				c.deleteNote();
			}
		});
	}
	c.addDash = function(){
		if(flg==0 && c.data.huy.length==7){
				c.data.huy += '-';
			flg = 1;
		}
		else if(flg==1 && c.data.huy.length==6){
			flg = 0;
		}

	}
}

sp_widget.css:
.flex{
	display:flex; 
  align-items:center; 
  justify-content: space-between;
}
.dateBox{
	width:25px;
  height:20px;
}
